    WebPanda.magnificPopup = function () {

        /* Magnific Popup JS BOC
         ====================================================== */
        var popupImage = $('.popup-image'),
                popupImageNoMargin = $('.popup-image-no-margins'),
                popupGallery = $('.popup-gallery'),
                zoomGallery = $('.zoom-gallery'),
                popupWithForm = $('.popup-with-form'),
                popupModal = $('.popup-modal'),
                popupWithZoomAnim = $('.popup-with-zoom-anim'),
                popupWithMoovAnim = $('.popup-with-move-anim'),
                popupIframe = $('.popup-youtube, .popup-vimeo, .popup-gmaps');

        // Single Image
        popupImage.magnificPopup({type: 'image'});

        // iFrame (Youtube, iFrma Video, Map)
        popupIframe.magnificPopup({
            disableOn: 700,
            type: 'iframe',
            mainClass: 'mfp-fade',
            closeOnContentClick: true,
            closeBtnInside: false,
            fixedContentPos: true,
            removalDelay: 160,
            preloader: false
        });

        /* Popup Group iFrame JS EOC */

        // Single Image Zoom
        popupImageNoMargin.magnificPopup({
            type: 'image',
            closeOnContentClick: true,
            closeBtnInside: false,
            fixedContentPos: true,
            mainClass: 'mfp-no-margins mfp-with-zoom', // class to remove default margin from left and right side
            image: {
                verticalFit: true
            },
            zoom: {
                enabled: true,
                duration: 300 // don't forget to change the duration also in CSS
            }
        });

        /* Single Image Zoom JS EOC */

        // Lightbox Gallery
        popupGallery.magnificPopup({
            delegate: '.popup-link',
            type: 'image',
            closeOnContentClick: true,
            closeBtnInside: false,
            fixedContentPos: true,
            tLoading: 'Loading image #%curr%...',
            mainClass: 'mfp-fade', // call class from css
            gallery: {
                enabled: true,
                navigateByImgClick: true,
                preload: [0, 1] // Will preload 0 - before current, and 1 after the current image
            },
            image: {
                tError: '<a href="%url%">The image #%curr%</a> could not be loaded.',
                titleSrc: function (item) {
                    return item.el.attr('title') + '<a target="_blank" href="' + item.el.attr('data-link') + '" class="link">Visit Website</a><small>by manthan</small>';
                }
            }
        });

        /* Lightbox Gallery JS EOC */

        // Lightbox Gallery Zoom
        zoomGallery.magnificPopup({
            delegate: 'a',
            type: 'image',
            mainClass: 'mfp-with-zoom mfp-img-mobile',
            fixedContentPos: true,
            closeBtnInside: false,
            image: {
                verticalFit: true,
                titleSrc: function (item) {
                    return item.el.attr('title') + '<small>by WebPanda</small>';
                }
            },
            gallery: {
                enabled: true
            },
            zoom: {
                enabled: true,
                duration: 300, // don't foget to change the duration also in CSS
                opener: function (element) {
                    return element.find('img');
                }
            }
        });

        /* Lightbox Gallery Zoom JS EOC */

        /* Lightbox With Form JS BOC */
        popupWithForm.magnificPopup({
            type: 'inline',
            preloader: false,
            closeBtnInside: false,
            fixedContentPos: true,
            mainClass: 'mfp-fade',
            focus: '#name',
            callbacks: {
                beforeOpen: function () {
                    if (700 > $(window).width()) {
                        this.st.focus = false;
                    } else {
                        this.st.focus = '#name';
                    }
                }
            }
        });

        /* Lightbox With Form JS EOC */

        /* Lightbox Dialog Simple JS BOC */
        popupModal.magnificPopup({
            type: 'inline',
            preloader: false,
            modal: true,
            blackbg: true,
            mainClass: 'mfp-fade',
            callbacks: {
                open: function () {
                    $html.css('margin-right', 0);
                }
            }
        });
        $document.on('click', '.popup-modal-dismiss', function (e) {
            e.preventDefault();
            $.magnificPopup.close();
        });

        /* Lightbox Dialog Simple JS EOC */

        /* Lightbox Dialog Animation JS BOC */
        popupWithZoomAnim.magnificPopup({
            type: 'inline',
            fixedContentPos: false,
            fixedBgPos: true,
            overflowY: 'auto',
            closeBtnInside: true,
            preloader: false,
            midClick: true,
            removalDelay: 300,
            blackbg: true,
            mainClass: 'my-mfp-zoom-in'
        });

        $('.popup-with-move-anim').magnificPopup({
            type: 'inline',
            fixedContentPos: false,
            fixedBgPos: true,
            overflowY: 'auto',
            closeBtnInside: true,
            preloader: false,
            midClick: true,
            removalDelay: 300,
            blackbg: true,
            mainClass: 'my-mfp-slide-bottom'
        });

        /* Lightbox Dialog Animation JS BOC */
    };
