/* Custom Google Map JS BOC
 ==============================================================  */
function generatemap(elementid, lat, lang, address) {

    var mapElement = document.getElementById(elementid);
    var mapMarker,
            colorPri;
    if (null != mapElement) {
        google.maps.event.addDomListener(window, 'load', init);
    }

    // Map Icon
    mapMarker = '/images/map-marker.png';
    colorPri = '#F12D34';

    function init() {
        var myLatLng = new google.maps.LatLng(lat, lang);
        var mapOptions = {
            zoom: 10, // Controls ZOOM

            // Map Settings
            disableDefaultUI: true,
            scrollwheel: true,
            scaleControl: true,
            // The latitude and longitude to center the map (always required)
            center: myLatLng, // Miami

            styles: [{
                    stylers: [{
                            hue: '#424449'
                        }, {
                            invert_lightness: true
                        }, {
                            saturation: -88
                        }, {
                            lightness: 36
                        }, {
                            gamma: 0.5
                        }]
                }, {
                    featureType: 'water',
                    elementType: 'geometry'
                }]
        };

        // Maps Address
        var map = new google.maps.Map(mapElement, mapOptions);
        var infoWindow = new google.maps.InfoWindow({
            content: '<div style="width:100%;float:left;"><div style="float:left;"><p style="font-size:14px;text-align:center;"><strong>' + address + '</strong></p></div></div>'
        });

        var marker = new google.maps.Marker({
            position: myLatLng,
            map: map,
            icon: mapMarker
        });
        google.maps.event.addListener(marker, 'click', function () {
            infoWindow.open(map, this);
        });
    }
}

/* Custom Rotate Text Fade Slider JS
 ====================================================== */
(function ($) {
    $.fn.extend({
        rotaterator: function (options) {
            var defaults = {
                fadeSpeed: 1000,
                pauseSpeed: 700,
                child: null
            };
            var options = $.extend(defaults, options);
            var next = '';
            return this.each(function () {
                var o = options;
                var obj = $(this);
                var items = $(obj.children(), obj);
                items.each(function () {
                    $(this).hide();
                });
                if (!o.child) {
                    next = $(obj).children(':first');
                } else {
                    next = o.child;
                }
                $(next).fadeIn(o.fadeSpeed, function () {
                    $(next).delay(o.pauseSpeed).fadeOut(o.fadeSpeed, function () {
                        var next = $(this).next();
                        if (0 == next.length) {
                            next = $(obj).children(':first');
                        }
                        $(obj).rotaterator({child: next, fadeSpeed: o.fadeSpeed, pauseSpeed: o.pauseSpeed});
                    });
                });
            });
        }
    });
}(jQuery));
